if(jQuery)(function($){
	$.extend($.fn, {
		qSelect: function(o) {
			_this = this;
			
			// 当前 select 框的 id 号
			var _this_id = $(_this).attr('id');
			// qSelect box 的 id 方便独自引用
			var qselect_id = 'qselect_' + _this_id;
			// 存放 select 框文本的 id 号
			var _this_text_id = _this_id + '_text';
			var _this_hidden_id = _this_id + '_hidden';
			// 是否为第一次触发模拟文本框
			var is_first_select = true;
			// select 的初始选项值
			var option = new Object();
			
			// 检查对象的类型
			isTypeSelect();
			// 加载基本的 html
			appendHtml();

			// 原来的 select
			$(_this)
				.css({'display' : 'none'})
				.after('<input type="text" id="' + _this_text_id + '" /><input type="hidden" id="' + _this_hidden_id + '" />');
			// 文本框
			// 下拉菜单的值
			var val_1 = $('#' + _this_id).val();
			// 数据库取出的该域的值
			var val_2 = $('#_select_value_' + _this_id).val();
			$('#' + _this_text_id).attr({
				name : _this_text_id,
				value : val_1 == val_2 ? $('#' + _this_id + ' option:selected').text() : val_2
			});
			// 隐藏框
			$('#' + _this_hidden_id).attr({
				name : _this_hidden_id,
				value : $('#' + _this_id).val()
			});
			
			getOption();
			createList(option);
			
			$('#' + _this_text_id).focus(function(){
				if($('#' + qselect_id).css('display') == 'none')
				{
					hideAllList();
					showList();
					searchList();
				}
			});
			
			$('#' + _this_text_id).keyup(function(){
				searchList();
			});
			
			$('#' + _this_text_id).blur(function(){
				var active_element = document.activeElement;
				// active_element.id != _this_text_id
				if(active_element.className != 'qselect_list')
				{
					hideList();
				}
			});
			
			$('#' + qselect_id + ' .qselect_list').blur(function(){
				var active_element = document.activeElement;
				if(active_element.id != _this_text_id)
				{
					hideList();
				}
			});
			
			// 检查对象的类型
			function isTypeSelect()
			{
				if($(_this).attr('type') != 'select-one')
				{
					alert('qSelect 的对象应该是 "单选菜单(select)"');
					return false;
				}
			}
			
			function appendHtml()
			{
				var html_code = '<div id="' + qselect_id + '" class="qselect_main ui_box"><iframe class="qselect_iframe" frameborder="0" src=""></iframe>';
				html_code += '<div class="qselect_message ui-widget">提示信息:请输入拼音或汉字查询</div><div class="qselect_list"><ul></ul></div></div>';
				$('body').append(html_code);
			}
			
			function getOption()
			{
				$.each($('#' + _this_id + ' option'), function(i){
					option[i] = {
						'name' : $(this).text(),
						'value' : $(this).attr('value'),
						'pinyin' : toPinyin($(this).text())
					};
				});
			}
			
			
			// 生成搜索结果
			function searchList()
			{
				// 初次显示全部数据
				if(is_first_select == false)
				{
					var input_value = $('#' + _this_text_id).val();
					var search_option = new Array();
					for(var i in option)
					{
						if(option[i]['name'].indexOf(input_value) > -1 || option[i]['pinyin'].indexOf(input_value) > -1)
						{
							search_option[i] = option[i];
						}
					}
					if(search_option.length == 0)
					{
						$('#' + qselect_id + ' .qselect_message').html('对不起,没有找到: ' + input_value);
					} else {
						$('#' + qselect_id + ' .qselect_message').html('提示信息:请输入拼音或汉字查询');
						createList(search_option);
					}
				} else {
					createList(option);
					is_first_select = false;
				}
			}
			
			// 显示列表
			function showList()
			{
				// 位置
				var this_offset = $('#' + _this_text_id).offset();
				var this_height = $('#' + _this_text_id).height(); 
				var this_width = $('#' + _this_text_id).width();
				$('#' + qselect_id).css({
					'left' : this_offset.left + 'px',
					// border ?
					'top' : (this_offset.top + this_height + 3) + 'px',
					'width' : this_width + 2 + 'px'
				});
				$('#' + qselect_id + ' iframe').css({
					'width' : this_width + 'px',
					'height' : parseInt($('#' + qselect_id + ' .qselect_list').css('height')) + parseInt($('#' + qselect_id + ' .qselect_message').css('height'))
				});
				$('#' + qselect_id).css({
					'height' : parseInt($('#' + qselect_id + ' .qselect_list').css('height')) + parseInt($('#' + qselect_id + ' .qselect_message').css('height')) + 1
				});
				$('#' + qselect_id).slideDown('slow');
			}
			
			// 隐藏当前 id 的下拉菜单
			function hideList()
			{
				$('#' + qselect_id)
				.animate({'height': '0', 'opacity' : 'hide'}, 'slow');
			}
			
			// 隐藏所有的下拉菜单
			function hideAllList()
			{
				$('.qselect_main')
				.animate({'height': '0', 'opacity' : 'hide'}, 'slow');
			}
			
			// 创建列表
			function createList(option)
			{
				var option_html = '';
				for(var i in option)
				{
					option_html += '<li value="' + option[i]['value'] + '"><span class="qselect_list_name">' + option[i]['name'] + '</span><a href="javascript:void(0);" title="' + option[i]['pinyin'] + '">' + option[i]['pinyin'] + '</a></li>';
				}
				$('#' + qselect_id + ' .qselect_list ul').html(option_html);
				
				
				// 绑定 hover
				$('#' + qselect_id + ' .qselect_list ul li').hover(
					function () {
						$(this).addClass('ui-state-highlight');
					},
					function () {
						$(this).removeClass('ui-state-highlight');
					}
				);
				
				// 绑定点击
				$('#' + qselect_id + ' .qselect_list ul li').click(function(){
					// _this_id 的类型为 select , 加入后可能不是原值 
					$('#' + _this_id).val($(this).attr('value'));
					// 原值
					$('#' + _this_hidden_id).val($(this).attr('value'));
					$('#' + _this_text_id).val($(this).find('span').html());
					hideList();
				});
			}
		}
	});
})(jQuery);